import { AfterViewInit, Component, OnInit } from '@angular/core';
import { FormBuilder, FormControl, Validators } from '@angular/forms';
import { ToastrService } from 'ngx-toastr';
import { Category } from 'src/app/models/Category';
import { Subcategory } from 'src/app/models/Subcategory';
import { CategoryService } from 'src/app/services/category/category.service';
import { ProductService } from 'src/app/services/product/product.service';
import { SubcategoryService } from 'src/app/services/subcategory/subcategory.service';

@Component({
  selector: 'app-new-product',
  templateUrl: './new-product.component.html',
  styleUrls: ['./new-product.component.css']
})
export class NewProductComponent implements OnInit, AfterViewInit {
  constructor(private formBuilder: FormBuilder,
              private subcategoryService: SubcategoryService,
              private categoryService: CategoryService,
              private productService: ProductService,
              private toastr: ToastrService) {}
  ngAfterViewInit(): void {

    if (this.getSavedBarcode()!.length > 10) {
      this.setBarcode(this.getSavedBarcode()!)
      this.addNewForm.controls.barcode.setValue(this.getSavedBarcode()!);
  }
  }

  subcategories: Subcategory[] = [];
  subcategoriesFilter: Subcategory[] = [];
  categories: Category[] = [];
  barcodeIsClean: boolean = true;
  subcategoryIsEmpty: boolean = true;
  firstSubcategory: number = 0;

  ngOnInit(): void {
    this.subcategoryService.getAll().subscribe(data => {
      this.subcategories = data;
      this.subcategoriesFilter = data;
    });
    this.categoryService.getAll().subscribe(data => this.categories = data);
  }

  name = new FormControl('', [
    Validators.required,
    Validators.minLength(3),
    Validators.maxLength(100),
  ]);

  description = new FormControl('', [
    Validators.maxLength(200),
  ]);

  madeIn = new FormControl('', [
    Validators.required,
    Validators.minLength(2),
    Validators.maxLength(60),
  ]);

  barcode = new FormControl('', [
    Validators.required,
    Validators.minLength(10),
    Validators.maxLength(20),
  ]);

  warningCount = new FormControl(0, [
    Validators.required
  ]);

  adminId = new FormControl('');


  categoryId = new FormControl('', [
    Validators.required,
    Validators.nullValidator,
  ]);

  subcategoryId = new FormControl('', [
    Validators.required,
    Validators.nullValidator,
  ]);

  public addNewForm = this.formBuilder.group({
    name: this.name,
    categoryId: this.categoryId,
    subcategoryId: this.subcategoryId,
    description: this.description,
    madeIn: this.madeIn,
    barcode: this.barcode,
    warningCount: this.warningCount,
    adminId: this.adminId
  });

  focusOnScan() {
    var element = document.getElementById('barcodeInput') as HTMLInputElement;
    element.focus();
    this.saveBarcode('');
  }

  scanned(event: any) {
    if(event.keyCode === 13){
      event.preventDefault();
      var barcode = document.getElementById('barcodeInput') as HTMLInputElement;
      this.setBarcode(barcode.value)
    }
  }

  categorySelected() {
    var categoryId = this.addNewForm.getRawValue().categoryId;
    this.subcategoriesFilter = this.subcategories.filter(s => s.categoryId == Number(categoryId))
    if (this.subcategoriesFilter.length > 0) {
      this.firstSubcategory = 
      this.subcategoryIsEmpty = false;
    }else {
      this.subcategoryIsEmpty = true;
    }
  }

  add() {
    this.productService.add(this.addNewForm.getRawValue())
  }

  randomBarcode() {
    this.productService.getBarcode().subscribe(data => {
      if (data) {
        this.setBarcode(data)
        this.addNewForm.controls.barcode.setValue(data.toString());
        this.toastr.success("Barcode yaratildi!");
        this.saveBarcode(data);
      }
      else {
        this.toastr.error("Xatolik yuz berdi!");
      }
    });
  }

  getCurrentBarcode() {
    return this.addNewForm.controls.barcode.value;
  }

  clearBarcode() {
    var src = 'assets/images/barcode0.png';
    var barcodeimg = document.getElementById('barcode') as HTMLImageElement
    barcodeimg.src = src;
    this.addNewForm.controls.barcode.setValue('');
    this.saveBarcode('');
  }

  setBarcode(barcode: string) {
    var barcodeImgUrl = 'https://barcodeapi.org/api/128/';
    var barcodeimg = document.getElementById('barcode') as HTMLImageElement
    barcodeimg.src = barcodeImgUrl + barcode;
    this.saveBarcode(barcode);
  }

  saveBarcode(barcode: string) {
    localStorage.setItem('barcode', barcode);
  }

  getSavedBarcode() {
    return localStorage.getItem('barcode')
  }
}
<div class="container">
  <div class="card">
    <h3 class="card-header">Yangi mahsulot qo'shish</h3>
    <div class="card-body">
      <form [formGroup]="addNewForm" class="modal-body">
        <div class="row mb-4">
          <div class="col-md-6">
            <div class="mb-3 mt-1">
              <label class="form-label">Barcode</label>
              <input
                type="text"
                class="form-control"
                placeholder="000000000000"
                ngModel
                id="barcodeInput"
                (keydown)="scanned($event)"
                formControlName="barcode"
              />
              <div
                *ngIf="barcode.errors?.['required']"
                class="form-text text-danger"
              >
                Maydonni to'ldiring!
              </div>
              <div
                *ngIf="barcode.errors?.['minlength']"
                class="form-text text-danger"
              >
                Kamida 10 ta belgidan iborat bo'lishi kerak!
              </div>
              <div
                *ngIf="barcode.errors?.['maxlength']"
                class="form-text text-danger"
              >
                20 ta belgidan oshmasligi kerak!
              </div>
            </div>
            <div class="d-flex justify-content-center">
              <button class="btn btn-primary mx-2" (click)="focusOnScan()">
                <i class="bi bi-upc-scan me-2"></i>Scanerlash
              </button>
              <button class="btn btn-success mx-2" (click)="randomBarcode()">
                <i class="bi bi-plus-square me-2"></i>Yangi yaratish
              </button>
            </div>
          </div>
          <div class="col-md-6" style="margin-top: 10px">
            <div id="printDiv">
              <div class="d-flex justify-content-center">
                <img
                  id="barcode"
                  src="assets/images/barcode0.png"
                  height="120px"
                  width="auto"
                  alt=""
                />
              </div>
              <div class="d-flex justify-content-center mt-3 links">
                <a class="btn btn-secondary" (click)="clearBarcode()"
                  ><i class="bi bi-arrow-counterclockwise me-2"></i> Tozalash</a
                >
                <a
                  [routerLink]="['/print', getCurrentBarcode()]"
                  class="btn btn-success"
                  ><i class="bi bi-printer-fill me-2"></i> Print</a
                >
              </div>
            </div>
          </div>
        </div>

        <hr />

        <div class="row">
          <div class="col-md-6">
            <div class="mb-1 mt-3">
              <label class="form-label">Mahsulot turi</label>
              <select
                class="form-select"
                formControlName="categoryId"
                (change)="categorySelected()"
              >
                <option value="null" disabled>
                  -- Mahsulot turini tanlang --
                </option>
                <option
                  *ngFor="let category of categories"
                  value="{{ category.id }}"
                >
                  {{ category.name }}
                </option>
              </select>
              <div
                *ngIf="categoryId.errors?.['required']"
                class="form-text text-danger"
              >
                Mahsulot turini tanlang!
              </div>
            </div>
            <div class="mb-3 mt-3">
              <label class="form-label">Nomi</label>
              <input
                type="text"
                class="form-control"
                placeholder="Mahsulot nomini kiriting"
                ngModel
                formControlName="name"
              />
              <div
                *ngIf="name.errors?.['required']"
                class="form-text text-danger"
              >
                Maydonni to'ldiring!
              </div>
              <div
                *ngIf="name.errors?.['minlength']"
                class="form-text text-danger"
              >
                Nom kamida 3 ta belgidan iborat bo'lishi kerak!
              </div>
            </div>
            <div class="mb-3 mt-3">
              <label class="form-label">Ishlab chiqarilgan joyi</label>
              <input
                type="text"
                class="form-control"
                placeholder="Masalan: Xitoy"
                ngModel
                formControlName="madeIn"
              />
              <div
                *ngIf="madeIn.errors?.['required']"
                class="form-text text-danger"
              >
                Maydonni to'ldiring!
              </div>
              <div
                *ngIf="madeIn.errors?.['minlength']"
                class="form-text text-danger"
              >
                Kamida 2 ta belgidan iborat bo'lishi kerak!
              </div>
              <div
                *ngIf="madeIn.errors?.['maxlength']"
                class="form-text text-danger"
              >
                60 ta belgidan oshmasligi kerak!
              </div>
            </div>
          </div>
          <div class="col-md-6">
            <div class="mb-1 mt-3">
              <label class="form-label">Kategoriyasi</label>
              <select class="form-select" id="subcategoryId" formControlName="subcategoryId">
                <option value="null" disabled>
                  -- Kategoriyani tanlang --
                </option>
                <option
                  value="{{ subcategory.id }}"
                  *ngFor="let subcategory of subcategoriesFilter"
                >
                  {{ subcategory.name }}
                </option>
              </select>
              <div
                *ngIf="subcategoryId.errors?.['required']&&subcategoryIsEmpty"
                class="form-text text-danger"
              >
                Kategoriyani tanlang!
              </div>
            </div>
            <div class="mb-3 mt-3">
              <label class="form-label">Izoh</label>
              <input
                type="text"
                class="form-control"
                placeholder="Mahsulotga izoh yozing"
                ngModel
                formControlName="description"
              />
              <div
                *ngIf="name.errors?.['required']"
                class="form-text text-success"
              >
                To'ldirish ixtiyoriy
              </div>
            </div>
            <div class="mb-3 mt-3">
              <label class="form-label">Ogohlantirish soni</label>
              <input
                type="number"
                class="form-control"
                placeholder="Ixtiyoriy"
                [value]="0"
                ngModel
                formControlName="warningCount"
              />
              <div
                *ngIf="warningCount.errors?.['required']"
                class="form-text text-danger"
              >
                Maydonni to'ldiring!
              </div>
            </div>
          </div>
        </div>

        <div class="modal-footer d-flex justify-content-center">
          <button type="button" class="btn btn-secondary" routerLink="..">
            Orqaga
          </button>
          <button
            [disabled]="!addNewForm.valid"
            type="submit"
            class="btn btn-primary"
            (click)="add()"
          >
            Saqlash
          </button>
        </div>
      </form>
    </div>
  </div>
</div>
