import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { ToastrService } from 'ngx-toastr';
import { Observable, of } from 'rxjs';
import { Warehouse } from 'src/app/models/warehouse';
import { WarehouseService } from 'src/app/services/warehouse/warehouse.service';

@Component({
  selector: 'app-warehouse',
  templateUrl: './warehouse.component.html',
  styleUrls: ['./warehouse.component.css']
})
export class WarehouseComponent implements OnInit{
  warehouses: Observable<Warehouse[]> | undefined;
  nameField: any;
  warehouseId: any;

  constructor(private warehouseService: WarehouseService,
              private toastr: ToastrService,              
              private formBuilder: FormBuilder,){}

  ngOnInit(): void {
    this.warehouses = this.warehouseService.getAll();
  }

  name = new FormControl('', [
    Validators.required,
    Validators.minLength(3),
    Validators.maxLength(100)
  ]);

  public addWarehouseForm = this.formBuilder.group({
    name: this.name
  });
  public editWarehouseForm: any;

  addNew() {
    this.warehouseService.add(this.addWarehouseForm.getRawValue());
  }

  remove(id: number) {
    this.warehouseService.remove(id);
  }

  setName(name: string, id: number) {
    this.nameField = new FormControl('', [
      Validators.required,
      Validators.minLength(3),
      Validators.maxLength(100),

    ]);
    this.warehouseId = new FormControl(id, [
      Validators.required
    ]);

    this.editWarehouseForm = this.formBuilder.group({
      name: this.nameField,
      id: this.warehouseId
    });

    return this.editWarehouseForm;
  }

  edit(warehouse: Warehouse) {
    this.warehouseService.edit(warehouse)
  }
}
