import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { ToastrService } from 'ngx-toastr';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';
import { Pagination } from 'src/app/models/Pagination';
import { Warehouse } from 'src/app/models/warehouse';

@Injectable({
  providedIn: 'root'
})
export class WarehouseService {

  constructor(private httpClient: HttpClient,
              private toastr: ToastrService) { }

  baseUrl: string ="https://localhost:44367/api/warehouse/";

  public pagination: Pagination = {
    HasPrevious: false,
    HasNext: false,
    TotalCount: 0,
    PageSize: 10,
    CurrentPage: 1,
    TotalPages: 0
  }

  getAll() : Observable<Warehouse[]> {
    return this.httpClient.get<Warehouse[]>(this.baseUrl);
  }

  getPaged(pageNumber: number): Observable<Warehouse[]> {


    const headers = new HttpHeaders().set('Authorization', `Bearer ${this.getToken()}`);

    const url = `${this.baseUrl}paged?pageSize=5&pageNumber=${pageNumber}`;

    return this.httpClient.get<any>(url, {headers, observe: 'response'})
      .pipe(
        map(response => {
          const warehouses = response.body as Warehouse[];
          const paginationHeader = response.headers.get('x-pagination');
          var xpagination = JSON.parse(paginationHeader??"");
          this.initPaginationParams(xpagination);
          return warehouses;
        })
      );
  }


  add(form: any) {
    this.httpClient.post(this.baseUrl, form).subscribe({ 
      next: data => {
        this.toastr.success("Muvofaqqiyatli qo'shildi!").onHidden.subscribe(() => {
          window.location.reload();
        });
        },
      error: error => {
          this.toastr.error("Qandaydir xatolik yuz berdi!");
        }});
  }

  edit(warehouse: Warehouse){
    this.httpClient.put(this.baseUrl, warehouse).subscribe({ 
      next: data => {
        this.toastr.success("Muvofaqqiyatli saqlandi!").onHidden.subscribe(() => {
          window.location.reload();
        });
        },
      error: error => {
          this.toastr.error("Qandaydir xatolik yuz berdi!");
        }});
  }

  remove(id: number) {
    this.httpClient.delete(this.baseUrl + id).subscribe({ 
      next: data => {
        this.toastr.success("Muvofaqqiyatli o'chirildi!").onHidden.subscribe(() => {
          window.location.reload();
        });
        },
      error: error => {
          this.toastr.error("Qandaydir xatolik yuz berdi!",'', {easeTime: 3000});
        }});
  }

  initPaginationParams(xpagination: any): void {
    this.pagination.HasPrevious = xpagination["HasPrevious"];
    this.pagination.HasNext = xpagination["HasNext"];
    this.pagination.TotalCount = xpagination["TotalCount"];
    this.pagination.CurrentPage = xpagination["CurrentPage"];
    this.pagination.PageSize = xpagination["PageSize"];
    this.pagination.TotalPages = xpagination["TotalPages"];
  }

  getToken() {
    var localdata = localStorage.getItem('data');
      var json = JSON.parse(localdata??"");
     return json["Token"];
  }
}
