import { Component, OnInit } from '@angular/core';
import { Observable } from 'rxjs';
import { Warehouse } from 'src/app/models/warehouse';
import { WarehouseService } from 'src/app/services/warehouse/warehouse.service';
import * as $ from 'src/assets/js';
import 'datatables.net';

@Component({
  selector: 'app-warehouse',
  templateUrl: './warehouse.component.html',
  styleUrls: ['./warehouse.component.css']
})
export class WarehouseComponent implements OnInit{
  warehouses: Observable<Warehouse[]> | undefined;

  constructor(private warehouseService: WarehouseService){}

  ngOnInit(): void {
    this.warehouses = this.warehouseService.getAll();
  }

}
<div class="d-flex justify-content-between">
    <h4 class="mb-0 text-uppercase mt-2">Omborlar jadvali</h4>
    <button class="btn btn-success">+ Yangi qo'shish</button>
</div>
<hr />
<div class="card">
  <div class="card-body">
    <div class="table-responsive">
      <table id="example2" class="table table-striped table-bordered">
        <thead>
          <tr>
            <th>#ID</th>
            <th>Nomi</th>
            <th>Yaratilgan vaqti</th>
            <th>Oxirgi tahrirlangan vaqti</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let item of warehouses | async">
            <td>{{item.id}}</td>
            <td>{{item.name}}</td>
            <td>{{item.addedDate}}</td>
            <td>{{item.modifiedDate}}</td>
            <td>
                <div class="table-actions d-flex align-items-center gap-3 fs-6">
                  <a href="javascript:;" class="text-warning" data-bs-toggle="tooltip" data-bs-placement="bottom" title="Edit"><i class="bi bi-pencil-fill"></i></a>
                  <a href="javascript:;" class="text-danger" data-bs-toggle="tooltip" data-bs-placement="bottom" title="Delete"><i class="bi bi-trash-fill"></i></a>
                </div>
              </td>
          </tr>

      </table>
    </div>
  </div>
</div>
