import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Router } from '@angular/router';
import { ToastrService } from 'ngx-toastr';
import { Constants } from 'src/app/models/Constants';

@Injectable({
  providedIn: 'root'
})
export class LoginService {

  url: string = Constants.BASE_URL + "auth/"

  constructor(private httpClient: HttpClient,
              private toastr: ToastrService,
              private router: Router) { }

  refreshToken() {
  var localdata = localStorage.getItem('data');
  if (localdata != null) {
    try {
        var json = JSON.parse(localdata??"");
        var token = json["Token"];
        var refreshToken = json["RefreshToken"];
        var body = {
          "token": token,
          "refreshToken": refreshToken
        }

        this.httpClient.post(this.url+'refresh-user', body, {withCredentials: true}).subscribe(
          { next: data => {
              localStorage.setItem("data", JSON.stringify(data));
                console.log(data)
              return true;
          },
          error: error => {
                console.log(error);
                return false;
              //  switch(error.status) {
              //    case 500: {
              //       return false;
              //    }break;
              //  }
          }}
      );
    }
    catch(error) {
      return false;
    }
  }

   return false;
  }


  login(form: any) {
    this.httpClient.post(this.url +'login', form, {withCredentials: true}).subscribe(
      { next: data => {
           localStorage.setItem("data", JSON.stringify(data));
           this.toastr.success('', 'Successfully logged in!');
           this.router.navigate(['/']);
       },
       error: error => {
        this.toastr.error(error.message)
         console.log(error);
          //  switch(error.status) {
          //    case 400: {
          //    }break;
          //    case 401: el.innerHTML = error.error; break;
          //    case 0: this.router.navigate(['/server-error']); break;
          //  }
       }}
   );
  }
}
