import { Component, OnInit } from '@angular/core';
import { ToastrService } from 'ngx-toastr';
import { Observable } from 'rxjs';
import { Warehouse } from 'src/app/models/warehouse';
import { WarehouseService } from 'src/app/services/warehouse/warehouse.service';

@Component({
  selector: 'app-warehouse',
  templateUrl: './warehouse.component.html',
  styleUrls: ['./warehouse.component.css']
})
export class WarehouseComponent implements OnInit{
  warehouses: Observable<Warehouse[]> | undefined;

  constructor(private warehouseService: WarehouseService,
              private toastr: ToastrService){}

  ngOnInit(): void {
    this.warehouses = this.warehouseService.getAll();
  }


  remove(id: number) {
    var result = this.warehouseService.remove(id);
    if (result) {
      this.toastr.success("Muvofaqqiyatli o'chirildi!");
    }
    else {
      this.toastr.error("Qandaydir xatolik yuz berdi!");
    }
  }
}
