import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { Warehouse } from 'src/app/models/warehouse';

@Injectable({
  providedIn: 'root'
})
export class WarehouseService {

  constructor(private httpClient: HttpClient,
              private toastr: ) { }

  baseUrl: string ="https://localhost:44367/api/warehouse/";

  getAll() : Observable<Warehouse[]> {
    return this.httpClient.get<Warehouse[]>(this.baseUrl);
  }

  add(form: any): boolean {
    this.httpClient.post(this.baseUrl, form).subscribe(
      { next: data => {
        return true;
    },
    error: error => {
      return false;
    }}
    );
    return false;
  }

  edit(){}

  remove(id: number): boolean {
    this.httpClient.delete(this.baseUrl + id).subscribe({ next: data => {
      return true;
  },
  error: error => {
    return false;
  }});

    return true;
  }
}
