import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { ToastrService } from 'ngx-toastr';
import { Observable, timeout } from 'rxjs';
import { map } from 'rxjs/operators';
import { Warehouse, WarehousePagination } from 'src/app/models/warehouse';

@Injectable({
  providedIn: 'root'
})
export class WarehouseService {

  constructor(private httpClient: HttpClient,
              private toastr: ToastrService) { }

  baseUrl: string ="https://localhost:44367/api/warehouse/";

  public hasPrevious: boolean = false;
  public hasNext: boolean = false;
  public TotalCount: number = 0;
  public PageSize: number = 10;
  public CurrentPage: number = 1;

  getAll() : Observable<Warehouse[]> {
    return this.httpClient.get<Warehouse[]>(this.baseUrl);
  }

  getPaged(pageNumber: number): Observable<Warehouse[]> {
    const url = `${this.baseUrl}paged?pageSize=10&pageNumber=${pageNumber}`;

    return this.httpClient.get<any>(url, { observe: 'response' })
      .pipe(
        map(response => {
          const warehouses = response.body as Warehouse[];
          const paginationHeader = response.headers.get('x-pagination');
          var xpagination =  JSON.parse(localStorage.getItem("data")??"");

          return warehouses;
        })
      );
  }


  add(form: any) {
    this.httpClient.post(this.baseUrl, form).subscribe({ 
      next: data => {
        this.toastr.success("Muvofaqqiyatli qo'shildi!").onHidden.subscribe(() => {
          window.location.reload();
        });
        },
      error: error => {
          this.toastr.error("Qandaydir xatolik yuz berdi!");
        }});
  }

  edit(warehouse: Warehouse){
    this.httpClient.put(this.baseUrl, warehouse).subscribe({ 
      next: data => {
        this.toastr.success("Muvofaqqiyatli saqlandi!").onHidden.subscribe(() => {
          window.location.reload();
        });
        },
      error: error => {
          this.toastr.error("Qandaydir xatolik yuz berdi!");
        }});
  }

  remove(id: number) {
    this.httpClient.delete(this.baseUrl + id).subscribe({ 
      next: data => {
        this.toastr.success("Muvofaqqiyatli o'chirildi!").onHidden.subscribe(() => {
          window.location.reload();
        });
        },
      error: error => {
          this.toastr.error("Qandaydir xatolik yuz berdi!",'', {easeTime: 3000});
        }});
  }
}
